(4,	'new_in_app_marketplace_2021',	'info',	'en_US',	'Customize your store with extensions',	'Check out our NEW Extensions tab to see our favorite extensions for customizing your store, and discover the most popular extensions in the WooCommerce Marketplace.',	'{}',	'unactioned',	'woocommerce.com',	'2021-11-29 20:05:23',	NULL,	0,	'plain',	'',	1,	'info',	1),
(5,	'wayflyer_bnpl_q4_2021',	'marketing',	'en_US',	'Grow your business with funding through Wayflyer',	'Fast, flexible financing to boost cash flow and help your business grow – one fee, no interest rates, penalties, equity, or personal guarantees. Based on your store’s performance, Wayflyer provides funding and analytical insights to invest in your business.',	'{}',	'pending',	'woocommerce.com',	'2021-11-29 20:05:23',	NULL,	0,	'plain',	'',	0,	'info',	1),
(6,	'wc_shipping_mobile_app_usps_q4_2021',	'marketing',	'en_US',	'Print and manage your shipping labels with WooCommerce Shipping and the WooCommerce Mobile App',	'Save time by printing, purchasing, refunding, and tracking shipping labels generated by <a href=\"https://woocommerce.com/woocommerce-shipping/\">WooCommerce Shipping</a> – all directly from your mobile device!',	'{}',	'pending',	'woocommerce.com',	'2021-11-29 20:05:23',	NULL,	0,	'plain',	'',	0,	'info',	1),
