(35,	'wc-admin-test-checkout',	'info',	'en_US',	'Non dimenticare di testare la procedura di acquisto',	'Assicurati che la procedura di acquisto funzioni correttamente prima di lanciare il tuo negozio. Completa la procedura di acquisto nella sua interezza: aggiunta di un prodotto al carrello, scelta di un indirizzo di spedizione ed effettuazione del pagamento.',	'{}',	'unactioned',	'woocommerce-admin',	'2021-11-29 21:58:16',	NULL,	0,	'plain',	'',	1,	'info',	1),
(36,	'woocommerce-core-paypal-march-2022-updated',	'update',	'en_US',	'Security auto-update of WooCommerce',	'<strong>Your store has been updated to the latest secure version of WooCommerce</strong>. We worked with WordPress to deploy PayPal Standard security updates for stores running WooCommerce (version 3.5 to 6.3). It’s recommended to disable PayPal Standard, and use <a href=\"https://woocommerce.com/products/woocommerce-paypal-payments/\" target=\"_blank\">PayPal Payments</a> to accept PayPal.',	'{}',	'pending',	'woocommerce.com',	'2024-01-02 10:30:24',	NULL,	0,	'plain',	'',	0,	'info',	1),
(37,	'woocommerce-core-paypal-march-2022-updated-nopp',	'update',	'en_US',	'Security auto-update of WooCommerce',	'<strong>Your store has been updated to the latest secure version of WooCommerce</strong>. We worked with WordPress to deploy security updates related to PayPal Standard payment gateway for stores running WooCommerce (version 3.5 to 6.3).',	'{}',	'pending',	'woocommerce.com',	'2024-01-02 10:30:24',	NULL,	0,	'plain',	'',	0,	'info',	1),
(38,	'pinterest_03_2022_update',	'marketing',	'en_US',	'Your Pinterest for WooCommerce plugin is out of date!',	'Update to the latest version of Pinterest for WooCommerce to continue using this plugin and keep your store connected with Pinterest. To update, visit <strong>Plugins &gt; Installed Plugins</strong>, and click on “update now” under Pinterest for WooCommerce.',	'{}',	'pending',	'woocommerce.com',	'2024-01-02 10:30:24',	NULL,	0,	'plain',	'',	0,	'info',	1),
(39,	'store_setup_survey_survey_q2_2022',	'survey',	'en_US',	'How is your store setup going?',	'Our goal is to make sure you have all the right tools to start setting up your store in the smoothest way possible.\r\nWe’d love to know if we hit our mark and how we can improve. To collect your thoughts, we made a 2-minute survey.',	'{}',	'pending',	'woocommerce.com',	'2024-01-02 10:30:24',	NULL,	0,	'plain',	'',	0,	'info',	1),
(40,	'woocommerce-payments-august-2022-need-to-update',	'update',	'en_US',	'Action required: Please update WooCommerce Payments',	'An updated secure version of WooCommerce Payments is available – please ensure that you’re using the latest patch version. For more information on what action you need to take, please review the article below.',	'{}',	'pending',	'woocommerce.com',	'2024-01-02 10:30:24',	NULL,	0,	'plain',	'',	0,	'info',	1),
